mapper.select:
SELECT writeno, somoimno, memberno, title, content, recom, cnt, replycnt, rdate, word
FROM s_write
WHERE writeno=#{writeno}

mapper.update:
UPDATE s_write
SET title=#{title}, content=#{content}, word=#{word}
WHERE writeno = #{writeno}

mapper.select:
SELECT COUNT(*) as cnt
FROM s_write
WHERE somoimno = #{somoimno}
